date_start = '1900-01-01';		}							$return = array();		$return['valid'] = false;		$return['message'] = "Internal Server Error"; 		$sp = "dbo.sp_rpt_absensi_karyawan";				if($print == 1){			$this->rpc_service->setSP(array("sp"=> $sp,"mode"=> $print == 1 ? "2" : "1","debug"=>"1"));		} else {			$this->rpc_service->setSP($sp);		}				$this->rpc_service->addField('karyawan_name',$karyawan_name);		$this->rpc_service->addField('karyawan_departemen',$departemen);		$this->rpc_service->addField('date',$date);		//$this->rpc_service->addField('date_end',$date_end);		$this->rpc_service->addField('format',$format);		$this->rpc_service->addField('temp_folder',sys_get_temp_dir());		$this->rpc_service->addField('sort',$sort);		$this->rpc_service->addField('limit',$limit);		$this->rpc_service->addField('offset',$offset);				$this->rpc_service->setWhere($search,$filterRules,$field);				if($print == 1){			$result = $this->rpc_service->Print_absensi_1('','','','',$format,$date,$karyawan_name,$departemen); 		   echo json_encode($result);		} else {			$this->authentication->plainlayout();									$result = $this->rpc_service->resultJSON_pop();				$data_result = json_decode($result['data'],true);		    //var_dump($data_result['detail']);			if(isset($data_result['detail']['result_count'])){				$records = $data_result['detail']['result_count'];				$total = ceil($data_result['detail']['result_count'] / $limit);			} else {				$records = 0;				$total = 0;			}						$responce = new stdclass();			$responce->page = $page;			$responce->records = $records;			$responce->total = $total;			$i=0; 			if($data_result){				if(isset($data_result['xrow'])){					foreach($data_result['xrow'] as $key=>$value){						foreach ($value as $k => $v) {							$responce->rows[$i][$k] = $v;						} 						$i++;					}				}			}					echo json_encode($responce);		}	     //$loaddata = $this->ecc_library-> get_field_data_pop($view,$field);			//var_dump($loaddata);		/