sult['xrow'])){					foreach($data_result['xrow'] as $key=>$value){						foreach ($value as $k => $v) {							$responce->rows[$i][$k] = $v;						} 						$i++;					}				}			}					echo json_encode($responce);		}	  }		function loaddata_awal()   {	  //karyawan/loaddata		$this->authentication->plainlayout();		$view = 'view_karyawan_absen';		$field = $this->absensi_table();	  				$return = array();		$return['valid'] = false;		$return['message'] = "Internal Server Error";	    $loaddata = $this->ecc_library-> get_field_data_pop($view,$field);		//	var_dump($loaddata);		echo $loaddata;	}					function post_add_edit() {		$this->authentication->plainlayout();		$parameter = array();		$return = array();		    $karyawan_id = isset($_POST['karyawan_id']) ? $_POST['karyawan_id'] : '';		$nama_karyawan = isset($_POST['nama_karyawan']) ? $_POST['nama_karyawan'] : '';		$gender = isset($_POST['gender_id']) ? $_POST['gender_id'] : '';		$nik = isset($_POST['nik']) ? $_POST['nik'] : '';		$badgenumber = isset($_POST['badgenumber']) ? $_POST['badgenumber'] : '';		$divisi = isset($_POST['divisi_id']) ? $_POST['divisi_id'] : '';		$departement = isset($_POST['departement_id']) ? $_POST['departement_id'] : '';		$status = isset($_POST['status_id']) ? $_POST['status_id'] : '';		$email = isset($_POST['email']) ? $_POST['email'] : '';		$jabatan = isset($_POST['jabatan_id']) ? $_POST['jabatan_id'] : '';		$date_in = isset($_POST['date_in']) ? $_POST['date_in'] : '';		$date_out = isset($_POST['date_out']) ? $_POST['date_out'] : '';		$group = isset($_POST['group']) ? $_POST['group'] : '';		$photo = isset($_FILES['file']) ? $_FILES['file'] : '';						//$purchase_order_no = isset($_POST['purchase_order_no']) ? $_POST['purchase_order_no'] : '';		//$purchase_order_date = isset($_POST['purchase_order_date']) ? $_POST['purchase_order_date'] : '';		//$partner_id = isset($_POST['partner_id']) ? $_POST['partner_id'] : '';		//$currencies_id = isset($_POST['curr