 isset($_POST['work_order_plan_id']) ? $_POST['work_order_plan_id'] : '';		$work_process_id = isset($_POST['work_process_id']) ? $_POST['work_process_id'] : '';						$user_id = $this->session->userdata('user_id');				$result = array();		$return['valid'] = false;		$return['status_code'] = 501;		$return['message'] = "Internal Server Error";				if(count($_POST) > 0){			if($work_order_request_id){				$this->rpc_service->setSP("dbo.sp_work_order_request_edit");				$this->rpc_service->addField('work_order_request_id',$work_order_request_id);			} else {				$this->rpc_service->setSP("dbo.sp_work_order_request_add");			}						$this->rpc_service->addField('work_order_request_no',$work_order_request_no);			$this->rpc_service->addField('work_order_request_date',$work_order_request_date);			$this->rpc_service->addField('work_order_plan_id',$work_order_plan_id);			$this->rpc_service->addField('work_process_id',$work_process_id);									$result = $this->rpc_service->resultJSON();									$data = array();			if(isset($result)){				if(isset($result['valid'])){					if($result['valid']){						if(isset($result['data'])){							$data = json_decode($result['data'],TRUE);							$work_order_request_id = $data['work_order_request_id'];														$return['valid'] = $result['valid'];							$return['status_code'] = $result['no'];							$return['message'] = $result['des'];							$return['work_order_request_id'] = $work_order_request_id;						}					} else {						$return['status_code'] = $result['no'];						$return['message'] = $result['des'];					}				}			}					} else {			$return['valid'] = false;			$return['message'] = "Session expired";		}				echo json_encode($return);	}		function loaddata_bom(){		$this->authentication->plainlayout();		$return = array();				$field = $this->work_order_request_bom_table();				$bom_id = isset($_POST['bom_id']) ? is_numeric($_POST['bom_id']) ? $_POST['bom_id'] : -1: -1;		$work_order_detail_id = isset($_POST['work_order_detail_id']) ? is_numeric($_POST['work_order_detail_id']) ? $_POST['work_order_detail_id'] : -1: -1;		$quantity_request = isset($_POST['quantity_request']) ? is_numeric($_POST['quantity_request']) ? $_POST['quantity_request'] : 0: 0;				$page = isset($_POST['page'])?$_POST['page']:1; // get the requested page         $rows = isset($_POST['rows'])?$_POST['rows']:10; // get how many rows we want to have into the grid         $sidx = isset($_POST['sidx'])?$_POST['sidx']:'r1'; // get index row - i.e. user click to sort         $sord = isset($_POST['sord'])?$_POST['sord']:'asc'; // get the direction 		$search = true; 		$filterRules =  isset($_POST['filters'])?$_POST['filters']:false;				$decode_filterRules = json_decode($filterRules,true);				if(isset($decode_filterRules)){			if(isset($decode_filterRules['rules'])){				$rules = $decode_filterRules['rules'];			} else {				$rules = array();			}		} else {			$decode_filterRules['groupOp'] = 'AND';			$rules = array();		}						$extra_param = array();		$extra_param['where']['0']['field'] = 'r7';		$extra_param['where']['0']['data'] = $work_order_detail_id;		$extra_param['where']['1']['field'] = 'r2';		$extra_param['where']['1']['data'] = $bom_id;						if(isset($extra_param['where'])){			foreach($extra_param['where'] as $dt_where){				$have_key = false;								foreach($rules as $key => $value){					if($value['field'] == $dt_where['field']){						$have_key = true;						$rules[$key]['data'] = $dt_where['data'];						break;					}				}								if(!$have_key){					$data_where = array();					$data_where['field'] = $dt_where['field'];					$data_where['op'] = 'eq';					$data_where['data'] = $dt_where['data'];					$rules[] = $data_where;				}			}		}				$decode_filterRules['rules'] = $rules;		$filterRules = json_encode($decode_filterRules);				$limit =  $rows;		$offset =  $rows * ($page - 1);				$order = isset($_REQUEST['order']) ? $_REQUEST['order'] : array();				if($sord == 'asc'){			$sord = 1;		} else {			$sord = 2;		}				$sort =	$sidx. '='.$sord;								$return = array();		$return['valid'] = false;		$return['message'] = "Internal Server Error";							$sp = "dbo.sp_ecc_load_view_data";			$this->rpc_service->setSP($sp);		$this->rpc_service->addField('view','view_work_order_request_bom');		$this->rpc_service->addField('sort',$sort);		$this->rpc_service->addField('limit',$limit);		$this->rpc_service->addField('offset',$offset);				$this->rpc_service->setWhere($search,$filterRules,$field);				$result = $this->rpc_service->resultJSON();			$data_result = json_decode($result['data'],true);				if(isset($data_result['detail']['result_count'])){			$records = $data_result['detail']['result_count'];			$total = ceil($data_result['detail']['result_count'] / $limit);		} else {			$records = 0;			$total = 0;		}				$responce = new stdclass();		$responce->page = $page;		$responce->records = $records;		$responce->total = $total;		$i=0; 		if($data_result){			if(isset($data_result['xrow'])){				foreach($data_result['xrow'] as $key=>$value){					foreach ($value as $k => $v) {						if($k == 'r5'){							if(isset($value['r8'])){								$responce->rows[$i][$k] = ($v * $quantity_request) * (1 + ($value['r8']/100));							} else {								$responce->rows[$i][$k] = ($v * $quantity_request);							}													} else {							$responce->rows[$i][$k] = $v;						}					} 															$i++;				}			}		}					$return = json_encode($responce);					echo $return;	}		function loaddata_detail(){		$this->authentication->plainlayout();				$work_order_request_id = isset($_REQUEST['work_order_request_id']) ? is_numeric($_REQUEST['work_order_request_id']) ? $_REQUEST['work_order_request_id']  : -1 : -1;		$methodid = isset($_REQUEST['methodid']) ? is_numeric($_REQUEST['methodid']) ? $_REQUEST['methodid']  : -1 : -1;				$view = 'view_work_order_request_detail';		$field = $this->work_order_request_