ult['valid'];							$return['status_code'] = $result['no'];							$return['message'] = $result['des'];												}					} else {						$return['status_code'] = $result['no'];						$return['message'] = $result['des'];					}				}			}					} else {			$return['valid'] = false;			$return['message'] = "Session expired";		}				echo json_encode($return);	}		function loaddata_detail_total(){		$this->authentication->plainlayout();				$work_order_request_id = isset($_REQUEST['work_order_request_id']) ? is_numeric($_REQUEST['work_order_request_id']) ? $_REQUEST['work_order_request_id']  : -1 : -1;		$methodid = isset($_REQUEST['methodid']) ? is_numeric($_REQUEST['methodid']) ? $_REQUEST['methodid']  : -1 : -1;				$view = 'view_work_order_request_list';		$field = $this->work_order_request_list_table();				$return = array();		$return['valid'] = false;		$return['message'] = "Internal Server Error";				$extra_param = array();		$extra_param['where']['0']['field'] = 'r2';		$extra_param['where']['0']['data'] = $work_order_request_id;		$extra_param['methodid'] = $methodid;				$loaddata = $this->ecc_library->get_field_data($view,$field,$extra_param);					echo $loaddata;	}		function delete_detail(){		$this->authentication->plainlayout();		$parameter = array();		$return = array();				$result = array();		$return['valid'] = false;		$return['status_code'] = 501;		$return['message'] = "Internal Server Error";				$work_order_request_detail_id = isset($_POST['work_order_request_detail_id']) ? $_POST['work_order_request_detail_id'] : '';		$user_id = $this->session->userdata('user_id');				if(count($_POST) > 0){						if($work_order_request_detail_id){				$this->rpc_service->setSP("dbo.sp_work_order_request_detail_delete");				$this->rpc_service->addField('work_order_request_detail_id',$work_order_request_detail_id);			}								$result = $this->rpc_service->resultJSON();			// print_r($result);						$data = array();			if(isset($result)